mapper.select:
select distinct co.course_name, co.teacher_id, co.room_id, co.class_id, co.usage_status_flag, th.teacher_name, rm.room_num, cd.class_name, cd.major, co.course_num, co.course_quantity, co.sort_status_flag from course co left join teacher th on th.teacher_id=co.teacher_id left join classroom rm on rm.room_id = co.room_id left join class_dto cd on cd.class_id = co.class_id left join schedule sh on co.course_id=sh.course_id
and co.course_id = #{courseId,jdbcType=DECIMAL}
and co.course_name like #{courseName,jdbcType=VARCHAR}
and co.teacher_id = #{teacherId,jdbcType=DECIMAL}
and co.room_id = #{roomId,jdbcType=DECIMAL}
and co.class_id = #{classId,jdbcType=DECIMAL}
and co.usage_status_flag = #{usageStatusFlag,jdbcType=DECIMAL}
and co.sort_status_flag = #{sortStatusFlag,jdbcType=DECIMAL}

select
sh.time_id,
sh.course_day,
sh.course_time,
th.teacher_name,
rm.room_num,
cd.class_name,
cd.major,

from course co
left join teacher th on th.teacher_id=co.teacher_id
left join classroom rm on rm.room_id = co.room_id
left join class_dto cd on cd.class_id = co.class_id
left join schedule sh on co.course_id=sh.course_id

    th.usage_status_flag = 1
    and rm.usage_status_flag = 1
    and cd.usage_status_flag = 1

        and co.course_id = #{courseId,jdbcType=DECIMAL}



        and co.course_name like #{courseName,jdbcType=VARCHAR}


        and co.teacher_id = #{teacherId,jdbcType=DECIMAL}


        and co.room_id = #{roomId,jdbcType=DECIMAL}


        and co.class_id = #{classId,jdbcType=DECIMAL}


        and co.usage_status_flag = #{usageStatusFlag,jdbcType=DECIMAL}


        and co.sort_status_flag = #{sortStatusFlag,jdbcType=DECIMAL}

select
th.teacher_name,
rm.room_num,
cd.class_name,
cd.major,

from course co
join schedule sh on co.course_id=sh.course_id
left join teacher th on th.teacher_id=co.teacher_id
left join classroom rm on rm.room_id = co.room_id
left join class_dto cd on cd.class_id = co.class_id
where
co.usage_status_flag = 1

    and co.teacher_id = #{course.teacherId,jdbcType=DECIMAL}


    and co.room_id = #{course.roomId,jdbcType=DECIMAL}


    and co.class_id = #{course.classId,jdbcType=DECIMAL}


    and sh.course_day = #{week.day,jdbcType=DECIMAL}


    and sh.course_time = #{week.time,jdbcType=DECIMAL}


    and co.sort_status_flag = #{co.sortStatusFlag,jdbcType=DECIMAL}
